import { Component } from '@angular/core';
import { cargarDatos } from 'src/app/services/cargarDatos.service';

@Component({
  selector: 'app-principal',
  templateUrl: './principal.component.html',
  styleUrls: ['./principal.component.css']
})
export class PrincipalComponent {

  historia:string="";

  constructor(private cargar:cargarDatos){
    this.cargar.cargarHistoria().subscribe((created:string)=>{
      this.historia=created;
    })
  }


}
<body>
   <section>
       <article class="post">
           <h2>Historia</h2>
           <p>Aqui va la historia</p>
           <p>{{historia}}</p>

           <p>La siembra del maíz es una práctica milenaria del pueblo Maya, caracterizada por las actividades ceremoniales que se realizan antes, durante y después de sembrar. El maíz es uno de los cultivos más antiguos de los pueblos ancestrales</p>

       </article>
   </section>
</body>
